import React, { useState } from "react";
import axios from "axios";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState("");
  const [columns, setColumns] = useState([]);
  const [dataPreview, setDataPreview] = useState([]);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setUploadStatus("");
    setColumns([]);
    setDataPreview([]);
    setQuestion("");
    setAnswer(null);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    setAnswer(null);
    const formData = new FormData();
    formData.append("file", selectedFile);
    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/upload/",
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setUploadStatus(`Success: ${response.data.detail}`);
      setColumns(response.data.columns || []);
      setDataPreview(response.data.preview || []);
    } catch (error) {
      setUploadStatus("Upload failed!");
      setColumns([]);
      setDataPreview([]);
    }
  };

  const handleAsk = async () => {
    if (!question) return;
    try {
      const response = await axios.post("http://127.0.0.1:8000/question/", {
        filename: selectedFile.name,
        question: question,
      });
      setAnswer(response.data.answer || "No answer provided.");
    } catch {
      setAnswer("Processing failed or invalid question.");
    }
  };

  return (
    <div style={{ margin: 40 }}>
      <h1>AI Data Agent â€“ Upload Excel File</h1>
      <input type="file" accept=".xlsx,.xls" onChange={handleFileChange} />
      <button onClick={handleUpload} disabled={!selectedFile}>
        Upload
      </button>
      <p>{uploadStatus}</p>
      {columns.length > 0 && (
        <div>
          <h2>Data Preview</h2>
          <table border="1">
            <thead>
              <tr>
                {columns.map((col) => (
                  <th key={col}>{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {dataPreview.map((row, idx) => (
                <tr key={idx}>
                  {columns.map((col) => (
                    <td key={col}>{row[col]}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>

          <h2>Ask a Question About Your Data</h2>
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            style={{ width: "400px" }}
            placeholder="Eg: What is the average age?"
          />
          <button style={{ marginLeft: "8px" }} onClick={handleAsk}>
            Ask
          </button>
          {answer && (
            <div style={{ marginTop: "20px" }}>
              <strong>Answer:</strong>
              <div>{answer}</div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
